/**
 * Returns new HTML element given a tagName. Options to add ID or classes.
 *
 * @param {string} tagName HTML tag name
 * @param {?string[]} [classList] Desired classes to add.
 * @param {?string} [id] ID for HTML element
 * @param {?string} [textContent] Text content inside new createElement
 * @returns {HTMLElement}
 */
var HTMLElem = function (tagName, classList, id, textContent) {
    var _a;
    var newHTMLElem = document.createElement(tagName);
    (_a = newHTMLElem.classList).add.apply(_a, classList);
    newHTMLElem.id = id;
    newHTMLElem.textContent = textContent;
    return newHTMLElem;
};
